/* @flow */

import type { TModules } from '@knit/knit-core';
import type { TPkgJson } from '@knit/needle';

const knit = require('@knit/knit-core');
const needle = require('@knit/needle');

type TCtx = {
  modules: TModules,
  pkgs: {[k: string]: TPkgJson},
};

const tasks = [
  {
    title: 'reading package.json of modules',
    task: (ctx: TCtx) => {
      ctx.pkgs = {};
      ctx.modules.forEach(m => {
        ctx.pkgs[m] = knit.readPkg(m, needle.paths);
      });
    },
  },
];

module.exports = tasks;
