/* @flow */

const Listr = require('listr');

const errors = require('@knit/nice-errors');
const log = require('@knit/logger');
const tasks = require('@knit/common-tasks');

type TArgv = {
  forceAll: boolean,
  skipPreflight: boolean,
};

module.exports = (argv: TArgv) => {
  new Listr([
    ...tasks.modules,
    ...tasks.preflight.knit,
    ...tasks.packages,
    ...tasks.updated,
    ...require('../tasks/build'),
  ], {
    renderer: log.getRenderer(),
    collapse: false,
  }).run({
    skipPreflight: argv.skipPreflight,
    forceAll: argv.forceAll,
  }).catch(errors.catchErrors);
};
