/* @flow */


import type { TModules } from '@knit/knit-core';

import readPkg from '@knit/read-pkg';

const Listr = require('listr');

const pathJoin = require('@knit/path-join');
const needle = require('@knit/needle');
const yarn = require('@knit/yarn-utils');

type TCtx = {
  modules: TModules,
  updated: TModules,
  script: string,
  args: Array<string>,
};

const tasks = [
  {
    title: 'npm run',
    task: (ctx: TCtx) => (
      new Listr(ctx.updated.map(m => ({
        title: m,
        skip: () => !(readPkg(m, needle.paths).scripts || {})[ctx.script],
        task: () => yarn.run(ctx.script, ctx.args, { cwd: pathJoin(needle.paths.modules, m) }),
      })), { concurrent: false })
    ),
  },
];

module.exports = tasks;
